import React, {useState, useEffect} from 'react'
import {Link,  useNavigate, useParams } from 'react-router-dom';
import { toast } from 'react-toastify';
import StudentService from '../services/StudentService';



const AddStudent = () => {

    const [name, setName] = useState('')
    const [phone, setPhone] = useState('')
    //const [emailId, setEmailId] = useState('')
    const history = useNavigate();
    const {id} = useParams();

    const saveOrUpdateStudent = (e) => {
        e.preventDefault();
        const student = {name, phone}
        if(id){
            StudentService.updateStudent(id, student).then((response) => {
                //console.log(response.data)
                history.push('/students');
                toast.success("student updated successfully");
            }).catch(error => {
                console.log(error)
            })

        }else{
            StudentService.createStudent(student).then((response) =>{
                console.log(response.data)
                history.push('/students');
                toast.success("student added successfully");

            }).catch(error => {
                console.log(error)
            })
        }

    }

    useEffect(() => {
        StudentService.getStudentById(id).then((response) =>{
            setName(response.data.name)
            setPhone(response.data.phone)
            //setEmailId(response.data.emailId)
        }).catch(error => {
            console.log(error)

        })
    }, [])

    const title = () => {

        if(id){
            return <h2 className = "text-center">Update student</h2>
        }else{
            return <h2 className = "text-center">Add student</h2>
        }
    }
    return (
        <>
            <div className="grid mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-success">Add Student</p>
                            <p className="fst-italic">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aliquam
                                atque consequuntur culpa cupiditate fugit libero molestias nemo neque nihil, nulla optio
                                possimus praesentium quasi repudiandae saepe sed sit, vitae! Nobis!</p>
                        </div>
                    </div>
                </div>
            </div>
            <div className="container">
                <div className="row">
                    <div className="col-sm-4">
                        <form>
                            <div className="mb-2">
                                <input type="text" className="form-control" placeholder="id"/>
                            </div>
                            <div className="mb-2">
                                <input type="text" className="form-control" placeholder="Name"/>
                            </div>
                            <div className="mb-2">
                                <input type="text" className="form-control" placeholder="RollNo"/>
                            </div>
                            {/* <div className="mb-2">
                                <input type="text" className="form-control" placeholder="Email"/>
                            </div> */}
                            {/* <div className="mb-2">
                                <input type="text" className="form-control" placeholder="Company"/>
                            </div> */}
                            {/* <div className="mb-2">
                                <input type="text" className="form-control" placeholder="Title"/>
                            </div> */}
                            {/* <div className="mb-2">
                                <select className="form-control">
                                    <option value="">Select Group</option>
                                </select>
                            </div> */}
                            <div className="mb-2">
                                <input type="submit" onClick= {handleClick} className="btn btn-success me-2" value="Create"/>
                                <Link to={'/about'} className="btn btn-dark">Cancel</Link>
                            </div>
                        </form>
                        <div className="list-group mt-3">



                        </div>
                    </div>
                </div> 
            </div>
        </>
    )
};
export default AddStudent;
