import React, {useState} from 'react';
import UserCard from './UserCard';
import UserList from './UserList';


let UserApp = () => {

    let[state, setState] = useState({
        selectedUser : {}
    });

    let {selectedUser} = state;

    let receiveUser = (user) => {
        setState((state) => ({

            selectedUser: user
        }));
    };

    return (
        <>
        {/* <pre>{JSON.stringify(selectedUser)}</pre> */}

         <div className="container mt-3">
             <div className="row">
                 <div className="col">
                     <p className="h3 text-primary">User App</p>
                     <div class="card mb-3">
  <img class="card-img-top" src="https://www.mystiquehomesandconstruction.com.au/wp-content/uploads/2020/07/Website-Frontpage-Header-02.jpg" alt=""  height={"60px"}/>
  <div class="card" style="width: 100rem;">
  <div class="card-body ">

  </div>
  </div>                
                 </div>
             </div>
         </div>
         </div>
            <div className="container mt-3">
                <div className="row">
                    <div className="col-md-9">
                        <UserList sendUser={receiveUser}/>
                    </div>
                    <div className="col-md-3">
                        <UserCard selectedUser={selectedUser}/>
                    </div>
                </div>
            </div>

        </>
    )
};
export default UserApp;
