import React from 'react';
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { toast } from "react-toastify";
// import {Form, Alert, InputGroup, Button,Table} from "react-bootstrap";
import CustomButton from "../CustomButton";
import {Link} from "react-router-dom";

import {addStudent, getStudents} from "../Server/servercall";

const AddStudent = () => {
    // let navigate = useNavigate();


    let [students,setStudents]=useState([])

    useEffect(()=>{

      addStudent({
        id:1313,
        name:"charles",
        rollNo:"6789Hw0"

      }).then(
        (data)=>{
          toast.success("student added  with id "+data.id)

        }
      ).catch((error)=>{
        toast.error("student not added")
      })


    },[])

    const handleClick=()=>{
      getStudents().then((data)=>{
        setStudents(data)
        console.log(data)
        toast.success("student loaded")
      }).catch(error=>{
        console.log(error);
        toast.error("students not loaded")
      })
    }


    const deleteHandler = async (id) => {
        toast.error('A book got deleted!!', {
          position: "bottom-center",
          autoClose: 3000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          });

        await servercall.deleteStudent(id);
        getStudents();


      };
