        <thead>
          <tr>
            <th>#Id</th>
            <th>Book Name</th>
            <th>Book Author</th>
            <th>ISBN</th>
            <th>Category</th>
            {/* <th>Status</th> */}
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {students.map((doc, index) => {
            return (
              <tr key={doc.id}>
                <td>{index + 1}</td>
                <td>{doc.bookname}</td>
                <td>{doc.author}</td>
                <td>{doc.isbn}</td>
                <td>{doc.category}</td>
                {/* <td>{doc.status}</td> */}
                <td>
                  <Button
                    variant="secondary"
                    className="edit"
                    onClick={(e) => getStudentId(doc.id)}
                  >
                    Edit
                  </Button>
                  <Button

                    variant="danger"
                    className="delete"
