import React, {useState} from 'react';

let TwoWayBinding = () => {

    let [username, setUsername] = useState("");
    let [password, setPassword] = useState("");
    let [email, setEmail] = useState("");

    let updateUsername = (event) => {
        setUsername(event.target.value);
    };

    let updatePassword = (event) => {
        setPassword(event.target.value);
    };

    let updateEmail = (event) => {
        setEmail(event.target.value);
    };

    return (
        <>
            <pre>username : {username}</pre>
            <pre>Password : {password}</pre>
            <div className="grid m-3">
                <div className="container">
                    <div className="row">
                        <div className="col-sm-4">
                            <div className="card shadow-lg">
                                <div className="card-body">
                                    <form>
                                        <div className="mb-2">
                                            <input onChange={updateUsername} type="text" className="form-control"
                                                   placeholder="username"/>
                                        </div>
                                        <div className="mb-2">
                                            <input onChange={updatePassword} type="password" className="form-control"
                                                   placeholder="password"/>
                                        </div>
                                        <div className="mb-2">
                                            <input onChange={updateEmail} type="text" className="form-control"
                                                   placeholder="email"/>
                                        </div>
                                    </form>

                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
};
export default TwoWayBinding;
