import React, {useEffect, useState} from 'react';
import {UserService} from "../services/UserService";
import Spinner from "../layout/Spinner";

let UserList = () => {

    let [state, setState] = useState({
        loading: false,
        users: [],
        errorMessage: null
    });

    useEffect(() => {
        // componentDidMount
        let response = null;
        setState({
            ...state,
            loading: true
        });
        let getUsersFromServer = async () => {
            try {
                response = await UserService.getAllUsers();
                setState({
                    ...state,
                    users: response.data,
                    loading: false
                });
            } catch (error) {
                setState({
                    ...state,
                    errorMessage: error.message,
                    loading: false
                });
            }
        }
        getUsersFromServer().then(r => r);
        return () => {
            // component will unMount
            response = null;
        }
    }, [state.users])

    let {loading, users, errorMessage} = state;
    return (
        <>
            <div className="grid mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-success">User List</p>
                            <p className="fst-italic">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aut
                                beatae deserunt distinctio doloribus ea earum error, esse explicabo labore magni,
                                nostrum qui reprehenderit sunt suscipit, tempora veritatis vitae voluptas
                                voluptatum?</p>
                        </div>
                    </div>
                    {
                        !loading && errorMessage ? <div className="container">
                            <div className="row">
                                <div className="col text-center">
                                    <p className="h5 text-danger">{errorMessage}</p>
                                </div>
                            </div>
                        </div> : null

                    }
                    {
                        loading && users.length === 0 ? <div className="container">
                            <div className="row">
                                <div className="col text-center">
                                    <p className="h2 text-danger">
                                        <Spinner/>
                                    </p>
                                </div>
                            </div>
                        </div> : null
                    }
                    <div className="row">
                        {
                            users.length > 0 && users.map(user => {
                                return (
                                    <div className="col-sm-3" key={user.id}>
                                        <div className="card my-2 shadow-lg">
                                            <div
                                                className="card-header bg-success  d-flex justify-content-between">
                                                <p className="card-title text-white">{user.name}</p>
                                                <p className="p-3 bg-warning">{user.id}</p>
                                            </div>
                                            <div className="card-body">
                                                <ul className="list-group">
                                                    <li className="list-group-item list-group-item-success">
                                                        Email : {user.email}
                                                    </li>
                                                    <li className="list-group-item list-group-item-success">
                                                        Phone : {user.phone}
                                                    </li>
                                                    <li className="list-group-item list-group-item-success">
                                                        Company : {user.company.name}
                                                    </li>
                                                </ul>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </>
    )
};
export default UserList;
