import React, {useState} from 'react';

let AuthUser = () => {
    let [state, setState] = useState({
        isLogin: false
    });

    let login = () => {
        setState({...state, isLogin: true});
    };

    let logOut = () => {
        setState({...state, isLogin: false});
    };

    let {isLogin} = state;
    return (
        <>
            <div className="grid m-3">
                <div className="container">
                    <div className="row">
                        <div className="col-sm-4">
                            <div className="card">
                                <div className="card-body">
                                    {
                                        (isLogin) ?

                                            <div>
                                                <p className="h2 text-success">Welcome User!</p>
                                                <p className="fst-italic">Lorem ipsum dolor sit amet, consectetur
                                                    adipisicing
                                                    elit. Aut eum ex laborum
                                                    odit sequi similique suscipit voluptas voluptatum? Blanditiis,
                                                    quisquam!</p>
                                            </div> :
                                            <div>
                                                <p className="h2 text-warning">Thank You!</p>
                                                <p className='fst-italic'>Lorem ipsum dolor sit amet, consectetur
                                                    adipisicing
                                                    elit. Aspernatur dicta
                                                    dolores eum maiores molestias nobis quisquam recusandae ut
                                                    voluptatibus
                                                    voluptatum.</p>
                                            </div>
                                    }


                                    {
                                        !isLogin ? <button onClick={login}
                                                           className="btn btn-success btn-sm me-2">Login</button> :
                                            <button onClick={logOut} className="btn btn-warning btn-sm">LogOut</button>
                                    }

                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
};
export default AuthUser;
