import React, {useState} from 'react';
import {CatalogueService} from "../services/CatalogueService";

let Catalogue = () => {
    let [state, setState] = useState({
        items: CatalogueService.getCatalogue()
    });

    let {items} = state;
    return (
        <>
            <div className="grid mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-success">Shopping Cart</p>
                            <p className="fst-italic">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ab animi
                                aperiam culpa impedit magni quibusdam repellat similique soluta tempore tenetur! Eaque
                                odio rem sit veritatis! Aut distinctio ea ipsam sunt.</p>
                        </div>
                    </div>
                    <div className="row">
                        {
                            items.map(item => {
                                return (
                                    <div className="col-sm-10 my-2" key={item.id}>
                                        <div className="card shadow-lg">
                                            <div className="card-body">
                                                <div className="row d-flex justify-content-around align-items-center">
                                                    <div className="col-2">
                                                        <img src={item.image} alt="" style={{width: "200px"}}/>
                                                    </div>
                                                    <div className="col-6">
                                                        <p className="h4">{item.name}</p>
                                                        <p className="h4">${item.price.toFixed(2)}</p>
                                                        <p className="h2">
                                                            {item.qty >= 10 ?
                                                                <span className="text-success">Available</span> : ""}
                                                            {item.qty < 10 && item.qty >= 1 ?
                                                                <span className="text-warning">Running Out</span> : ""}
                                                            {item.qty === 0 ?
                                                                <span className="text-danger">Out of Stock</span> : ""}
                                                        </p>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </>
    )
};
export default Catalogue;
