import React, { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom'

const Register = () => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();
    useEffect(() => {
        const auth = localStorage.getItem('user');
        if (auth) {
            navigate('/')
        }
    }, [])

    const collectData = async () => {
        console.warn(name, email, password);
        let result = await fetch("http://localhost:5000/register", {
            method: 'post',
            body: JSON.stringify({ name, email, password }),
            headers: {
                'Content-Type': 'application/json'
            }
        });
        result = await result.json();
        console.warn(result);
        localStorage.setItem("user", JSON.stringify(result.result))
        localStorage.setItem("token", JSON.stringify(result.auth))

        navigate('/')
    }

    return (
        <div className="card col-sm-4 register text-center offset-md-4 mt-5 p-3">
            <div className="card-header bg-warning text-white"><h3>
                <img src="data:image/jpeg;base64," alt="" style={{height:"80px",width:"80px"}}/>Register</h3></div>
            <div className="card-body bg-white">
            <div>
            <input className="inputBox mb-3" type="text" placeholder="Enter Name"
                value={name} onChange={(e) => setName(e.target.value)}
            />
            </div>
            <div>
            <input className="inputBox mb-3" type="text" placeholder="Enter Email"
                value={email} onChange={(e) => setEmail(e.target.value)}
            />
            </div>
            <div>
            <input className="inputBox mb-3" type="password" placeholder="Enter password"
                value={password} onChange={(e) => setPassword(e.target.value)}
            />
            </div>
            </div>
            <button onClick={collectData} className="btn btn-warning" type="button">Register</button>
        </div>
    )
}
export default Register
