import React, {useState} from 'react';
import Child from "./Child";

let Parent = () => {

    let [state, setState] = useState({
        message: 'Iam from parent',
        employees: []
    });

    let addEmployee = (employee) => {
        setState({...state, employees: [...state.employees, employee]});
    };

    let setMessage = (msg) => {
        setState({...state, childMsg: msg});
    };

    return (
        <>
            <div className="card">
                <div className="card-body bg-success text-white">
                    <h3>Parent Component</h3>
                    <pre>{JSON.stringify(state.employees)}</pre>
                    <Child message={state.message} setMessage={setMessage} addEmployee={addEmployee}/>
                </div>
            </div>

        </>
    )
};
export default Parent;
