import React, {useState} from 'react';

let Counter = () => {

    let [count, setCount] = useState(0);

    let increment = (value) => {
        count += value;
        setCount(count);
    };

    let decrement = () => {
        count--;
        setCount(count);
    };

    let decrementBy = () => {
        count -= 5;
        setCount(count);
    };

    return (
        <>
            <div className="grid m-3">
                <div className="container">
                    <div className="row">
                        <div className="col-sm-6">
                            <div className="card shadow-lg">
                                <div className="card-body">
                                    <p className="display-2">{count}</p>
                                    <button onClick={() => increment(1)} className="btn btn-success me-2">Increment
                                    </button>
                                    <button onClick={decrement} className="btn btn-warning me-2">Decrement</button>
                                    <button onClick={() => increment(5)} className="btn btn-primary me-2">Increment By 5
                                    </button>
                                    <button onClick={decrementBy} className="btn btn-dark me-2">Decrement By 5</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
};
export default Counter;
