import react,{useState} from "react"
import Categories from "./Categories";



const Category = () => {
    const [data, setData] = useState(Categories);

    return (
        <>
            <h1 className="text-center text-info" >Let's Shop</h1>
            <div className="container-fluid mx-2">
                <div className="row mt-5 mx-2">
                    <div className="col-md-3">
                        <button className="btn btn-warning w-100 mb-4">Men</button>
                        <button className="btn btn-warning w-100 mb-4">Wo-Men</button>
                        <button className="btn btn-warning w-100 mb-4">Kids-Wear</button>
                        <button className="btn btn-warning w-100 mb-4">white</button>
                        <button className="btn btn-warning w-100 mb-4">black</button>
                        <button className="btn btn-warning w-100 mb-4">All</button>


                    </div>
                    <div className="col-md-9">
                        <div className="row">
                            {data.map((values) =>{
                                return(

                                    <>
                                    <div className="col-md-4">
                                <div class="card mb-4">
                                    <img src="assets/cloths.jpg" className="card-img-top" />
                                    <div className="card-body">
                                        <h5 className="card-title">Card title</h5>
                                        <p className="">211/-</p>
                                        <h6 className="card-subtitle mb-2 text-muted">Card subtitle</h6>
                                        <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                        <button className="btn btn-dark text-white">buy now</button>
                                    </div>
                                </div>
                            </div>


                                 </>
                                )

                            })}

                            </div>
                    </div>

                </div>
            </div>

        </>


    );

};
export default Category;
