import React, {useState} from 'react';

let Register = () => {

    let [actions, setActions] = useState([

        "Buy",
        "Sell",
        "Trade",
        "Rent"
    ]);

    let [state, setState] = useState({
        user : {
            username: '',
            email: '',
            password: '',
            action:'',
            bio:'',
            terms: false

        }
    });

    let{user} = state;
    let updateInput = (e) =>{

        setState({
            ...state,
            user:{
                ...state.user,
                [e.target.name]: (e.target.name === "checkbox")? e.target.checked : e.target.value
            }
        }

        );

    };

    let register = (e) => {
        e.preventDefault();  //stops / prevents the page refresh
        console.log(user);
    }




    return (
        <>
            {/* <pre>{JSON.stringify(user)}</pre> */}

            <div className="grid m-3">
                <div className="container">
                    <div className="row">
                        <div className="col-sm-4">
                            <div className="card shadow-lg">
                                <div className="card-header bg-secondary text-light">
                                    <p className='h4'>Register</p>
                                </div>
                                <div className='card-body bg-light'>
                                    <form onSubmit = {register}> 
                                        <div className='mb-2'>

                                            <input 
                                            name ="username" 
                                            onChange={updateInput} 
                                            value = {user.username}
                                            type= "text" className='form-control' placeholder='Username'/>
                                        </div>
                                        <div className='mb-2'>
                                            <input 
                                              name ="email" 
                                             onChange={updateInput}
                                             value = {user.email} 
                                             type= "email" className='form-control' placeholder='Email'/>
                                        </div>
                                        <div className='mb-2'>
                                            <input
                                             name ="password" 
                                             onChange={updateInput}
                                             value = {user.password}  
                                            type= "password" className='form-control' placeholder='Password'/>
                                        </div>
                                        <div className='mb-2'>
                                            <select 
                                            name ="action" 
                                            onChange={updateInput}
                                            value = {user.action}  
                                            className='form-control'>
                                                <option value="">Select Action</option>
                                                {
                                                    actions.map(action =>{
                                                        return(
                                                            <option 
                                                            key={action}
                                                            value = {action}>{action}</option>
                                                        )
                                                    })
                                                }
                                            </select>

                                        </div>
                                        <div className='mb-2'>
                                            <textarea 
                                             name ="bio" 
                                             onChange={updateInput}
                                             value = {user.bio} 
                                            rows={3} className = 'form-control' placeholder='bio'/>
                                        </div>

                                        <div className='mb-2'>
                                            <input
                                             name ="terms" 
                                             onChange={updateInput} 
                                             value = {user.terms}
                                            type= "checkbox" className='form-check-input'/>
                                            <span className='form-check-label'> Terms & Conditions</span>
                                        </div>
                                        <div className='mb-2'>
                                            <input type="submit" className='btn btn-secondary' value="Register" />
                                        </div>
                                    </form>

                                </div>
                            </div>

                        </div>
                    </div>
                </div>
            </div>
        </>
    )
};
export default Register;
