import React, {useState} from 'react';
import {CartService} from "../services/CartService";

let ShoppingCart = () => {

    let [state, setState] = useState({
        items: CartService.getAllCartItems()
    });

    let incrementQty = (itemId) => {
        // setting new state data
        setState({
            ...state,
            items: state.items.map(item => {
                if (item.id === itemId) {
                    return {
                        ...item,
                        qty: item.qty + 1
                    }
                }
                return item;
            })
        });
    };

    let decrementQty = (itemId) => {
        // setting new state data
        setState({
            ...state,
            items: state.items.map(item => {
                if (item.id === itemId) {
                    return {
                        ...item,
                        qty: item.qty - 1 > 0 ? item.qty - 1 : 1
                    }
                }
                return item;
            })
        });
    };

    let deleteItem = (itemId) => {
        setState({
            ...state,
            items: state.items.filter(item => {
                return item.id !== itemId
            })
        })
    };

    let {items} = state;
    return (
        <>
            <div className="grid mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-success">Shopping Cart</p>
                            <p className="fst-italic">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ab animi
                                aperiam culpa impedit magni quibusdam repellat similique soluta tempore tenetur! Eaque
                                odio rem sit veritatis! Aut distinctio ea ipsam sunt.</p>
                        </div>
                    </div>
                    {
                        items.length > 0 ?
                            <div className="row mt-3">
                                <div className="col">
                                    <table className="table table-hover text-center table-striped">
                                        <thead>
                                        <tr>
                                            <th>SNO</th>
                                            <th>Image</th>
                                            <th>Name</th>
                                            <th>Price</th>
                                            <th>Qty</th>
                                            <th>Total</th>
                                            <th>Action</th>
                                        </tr>
                                        </thead>
                                        <tbody>
                                        {
                                            items.map(item => {
                                                return (
                                                    <tr key={item.id}>
                                                        <td>{item.id}</td>
                                                        <td>
                                                            <img src={item.image} alt="" style={{width: "50px"}}/>
                                                        </td>
                                                        <td>{item.name}</td>
                                                        <td>{item.price}</td>
                                                        <td>
                                                            <i className="fa fa-minus-circle me-2"
                                                               onClick={() => decrementQty(item.id)}
                                                            />
                                                            {item.qty}
                                                            <i className="fa fa-plus-circle ms-2"
                                                               onClick={() => incrementQty(item.id)}/>
                                                        </td>
                                                        <td>{item.qty * item.price}</td>
                                                        <td>
                                                            <i onClick={() => deleteItem(item.id)}
                                                               className="fa fa-trash-alt text-danger"/>
                                                        </td>
                                                    </tr>
                                                )
                                            })
                                        }
                                        </tbody>
                                    </table>
                                </div>
                            </div> : <div className="text-center">
                                <p className="h3 text-danger">Cart is Empty</p>
                            </div>
                    }
                </div>
            </div>
        </>
    )
};
export default ShoppingCart;
