import React, {useState} from 'react';
import UserCard from './UserCard';
import UserList from './UserList';


let UserApp = () => {

    let[state, setState] = useState({
        selectedUser : {}
    });

    let {selectedUser} = state;

    let receiveUser = (user) => {
        setState((state) => ({

            selectedUser: user
        }));
    };

    return (
        <>
        {/* <pre>{JSON.stringify(selectedUser)}</pre> */}

         <div className="container mt-3">
             <div className="row">
                 <div className="col">
                     <p className="h3 text-success">User App</p>
                     <div class="card mb-3">
  <img class="card-img-top" src="..." alt="Card image cap"/>
  <div class="card-body"></div>
                     <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Laudantium eum illum explicabo velit quas vitae nam fugit nostrum, ducimus, vero nihil maiores dignissimos perspiciatis voluptates iure iusto distinctio. Labore, eius?</p>
                 </div>
             </div>
         </div>
         </div>
            <div className="container mt-3">
                <div className="row">
                    <div className="col-md-9">
                        <UserList sendUser={receiveUser}/>
                    </div>
                    <div className="col-md-3">
                        <UserCard selectedUser={selectedUser}/>
                    </div>
                </div>
            </div>

        </>
    )
};
export default UserApp;
